@use 'sass:map';

// Shadows
$shadow-s: 0 4.4px 12px rgba(19, 16, 34, 0.08);
$shadow-m: 0 4.4px 12px rgba(19, 16, 34, 0.14);
$shadow-l: 0 4.4px 12px rgba(19, 16, 34, 0.2);
