}

/* Body text styling */
.body-text-lead, .body-text-xl, .body-text-l, .body-text-m,
.body-text-s, .body-text-xs {
  font-weight: 400;
}

.body-text-lead {
  font-size: 1.5rem;
}

.body-text-xl {
