  font-weight: map-get(a.$Font-weight, bold);
  display: none;
}

.btn-s{
  display: none;
}

  
.mobile-nav{
  display: none;
  justify-content: right;
  width: 15rem;
  position: absolute;
  top: 4.5rem;
  padding: 1rem;
  margin-right: 0.6rem;
  background-color: hsl(from var(--clr-background) h s l / 80%);
  border: 1px solid var(--clr-placeholder);
  border-radius: 1rem;
  #login{
    display: block;
    box-shadow: a.$shadow-primary-btn;
  }
  input{
